var ntc = require('../ntc.js');
var assert = require('should');

describe('ntc', function() {
    it('name', function() {
        var n_match  = ntc.name("#6195ED");
        n_match[0].should.equal('#6495ED');
        n_match[1].should.equal('Cornflower Blue');
        n_match[2].should.equal(false);
    });
    it('lookup', function() {
        ntc.value("Red").should.equal(0xFF0000);
    });    
});